package com.kn.stringMethods;

public class StringStartsAndEndsWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="ram is a boy";
		boolean isStartsWith=s.startsWith("r");
		System.out.println("String starts with r = "+isStartsWith);
		
		boolean isEndsWith=s.endsWith("boy");
		System.out.println("String ends with roy = "+isEndsWith);
		
				
		

	}

}
